import React, { useState, useEffect } from 'react';
import { getProducts } from '../../helpers/userFetch';
import Card from './Card';

const Home = () => {
  const [productsBySold, setProductBySold] = useState([]);
  const [productsByArrival, setProductByArrival] = useState([]);
  const [error, setError] = useState(false);
  const [loading, setLoading] = useState(true);

  const loadProductsBySold = () => {
    getProducts('sold').then(data => {
      if (data.error) {
        setError(data.error);
      } else {
        setLoading(false);
        setProductBySold(data);
      }
    });
  };

  const loadProductsByArrival = () => {
    getProducts('createdAt').then(data => {
      if (data.error) {
        setError(data.error);
      } else {
        setLoading(false);
        setProductByArrival(data);
      }
    });
  };

  useEffect(() => {
    loadProductsByArrival();
    loadProductsBySold();
    // eslint-disable-next-line
  }, []);

  const showError = () => error && <h2>Fail to load!</h2>;

  const showLoading = () =>
    loading && (
      <div className="d-flex justify-content-center">
        <div className="spinner-border" role="status">
          <span className="sr-only">Loading...</span>
        </div>
      </div>
    );

  return (
    <div>
      {showError()}
      <h1 className="title pt-3 pb-3">

      <image src ="https://www.google.com/url?sa=i&url=https%3A%2F%2Fstock.adobe.com%2Fsearch%3Fk%3D%2522new%2Barrival%2522&psig=AOvVaw0wsQfv-mHEfD1EEj0Dte4j&ust=1622391332783000&source=images&cd=vfe&ved=0CAIQjRxqFwoTCIjJ-uuk7_ACFQAAAAAdAAAAABAD" />





      </h1>
      {showLoading()}

      <div className="container-fluid row m-0 p-0 card-container">
        {productsByArrival.map((product, i) => (
          <div className="col-4 mb-3" key={i}>
            <Card product={product} />
          </div>
        ))}
      </div>

      <h1 className="title pt-3 pb-3">Best Sellers</h1>
      {showLoading()}
      <div className="container-fluid row m-0 p-0 card-container">
        {productsBySold.map((product, i) => (
          <div className="col-4 mb-3" key={i}>
            <Card product={product} />
          </div>
        ))}
      </div>
    </div>
  );
};

export default Home;
